import { useEffect, useState } from 'react';
import io from 'socket.io-client';
import Tweet from './components/Tweet';
import './App.css'

function App() {
  const [tweets, setTweets] = useState([])

  useEffect(() => {
    const socket = io.connect('http://localhost:3002/')
    socket.on('connect', () => {
      console.log('socket connect')
      socket.on('tweets', data => {
        setTweets((prev) => [data].concat(prev.slice(0, 20)))
        console.log(data)
      })
    })
    socket.on('disconnect', () => {
      socket.off('tweets')
      socket.removeAllListeners('tweets')
      console.log('socket disconnected')
    })

  }, [])

  return (
    <div className='flex'>
      {/* <img style={{position: 'absolute', top:0, left: 0}} width={550} src={Frame} alt=''/> */}

      <div className='fixedWidth'>
      {tweets.map((tweet) => <Tweet public_metrics={tweet.public_metrics} referenced_tweets={tweet.referenced_tweets} id={tweet.id} author={tweet.author} media={tweet.media} created_at={tweet.created_at} text={tweet.text} key={tweet} />)}
      </div>
    </div>
  );
}

export default App;
